import React from "react";
import { useState, useEffect,useRef } from "react";
import { useNavigate } from "react-router-dom";

function Submit(props){
    return (
        <button onClick={props.onChangeMode}>제출</button>
    );
}

function Question(props){
    const [inputs, setInputs] = useState({});
    const handleChange = (event) => {
    const { name, value } = event.target;
    setInputs((prevInputs) => ({
        prevInputs,
        [name]: value
    }));
    };

    const handleSubmit = (event) => {
    event.preventDefault();
    // 입력된 텍스트 값 확인
    console.log(inputs);
    };
    return (
        <div onClick=''>
            <h3>문제 {props.number}.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{props.value.problem}</h3>
            <input type='text' value={()=>set} onChange={handleChange}></input>
        </div>
    );
}

function MathTest(){
    const [questions, setQuestions] = useState([]);

    const myElementRef = useRef(null);

    const navigate = useNavigate();

    const handleClick = () => {
      if (myElementRef.current) {
        const elementValue = myElementRef.current.value;
        console.log('Class Value:', elementValue);
      }
    }

    useEffect(() => {
        const qRequest = async () => {
          const url = "https://mysql-server.azurewebsites.net/sachick";
          const data = {
            degree: 7
          };

          try {
            const response = await fetch(url, {
              method: "POST",
              headers: {
                "Content-Type": "application/json"
              },
              body: JSON.stringify(data)
            });

            const fetchedQuestions = await response.json();
            setQuestions(fetchedQuestions);
          } catch (error) {
            console.error("Error:", error);
          }
        };

        qRequest();
      }, []);

      console.log(questions);

    return (
        <div>
            <h2>수학 테스트</h2>
            {questions.map((question, index) => (
                <Question key={index} number={index+1} value={question} ref={myElementRef}></Question>
            ))}
            <Submit onChangeMode={handleClick}></Submit>
        </div>
    );
}

export default MathTest;
