import React from "react";
import './LoginBox.css';
import { useState } from "react";

function Login(props){
    return (
        <div class='loginButtonBox'>
            <button class='login' onClick={props.onChangeMode}>Login</button>
        </div>
    );
}

function LoginBox(){
    const [id, setId] = useState(null);
    const [pw, setPw] = useState(null);
    const [idValue, setIdValue] = useState("");
    const [pwValue, setPwValue] = useState("");

    const handleLogin = () => {
        setId(idValue);
        setPw(pwValue);
        setTimeout(() => {
          console.log(id); // 로그인 버튼 클릭 시 입력한 ID 값 출력
          console.log(pwValue); // 로그인 버튼 클릭 시 입력한 password 값 출력
        }, 0);
      };

    return (
        <div class='LoginBox'>
            <h1 class='Text'>Welcome!</h1>
            <div class='inputBox'>
                <div class='info'>
                    <text class='text'>ID</text>
                    <input type='text' value={idValue} onChange={(e) => setIdValue(e.target.value)} class='inputField' id='idText'></input>
                </div>
                <div class='info'>
                    <text class='text'>password</text>
                    <input type='text' value={pwValue} onChange={(e) => setPwValue(e.target.value)} class='inputField' id='passwordText'></input>
                </div>
                <Login onChangeMode={handleLogin}></Login>
            </div>
        </div>
    );
}

export default LoginBox;
